        ArrayList<Long> result = new ArrayList<Long>();
        for(int i = 0; i < arr.size(); i++) {
            for(int j = 0; j < arr.size() - 1; j++) {
                if(arr.get(j) < arr.get(j + 1)) {
                    long temp = arr.get(j);
                    arr.set(j, arr.get(j + 1));
                    arr.set(j + 1, temp);
                }
            }
        }
        for(int i = 0; i < k; i++) {
            result.add(arr.get(i));
        }
        return result;

