    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        if(k == 0) {
            return new ArrayList<Long>();
        }
        PriorityQueue<Long> pq = new PriorityQueue<Long>(new Comparator<Long>() {
            @Override
            public int compare(Long a, Long b) {
                return -Long.compare(a, b);
            }
        });
        for(int i = 0; i < arr.size(); i++) {
            if(pq.size() < k) {
                pq.add(arr.get(i));
            } else if(arr.get(i) > pq.peek()) {
                pq.poll();
                pq.add(arr.get(i));
            }
        }
        ArrayList<Long> ans = new ArrayList<Long>();
        while(!pq.isEmpty()) {
            ans.add(pq.poll());
        }
        return ans;

