    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        if (arr.size() <= k) {
            return arr;
        }
        ArrayList<Long> result = new ArrayList<Long>(arr.subList(0, (int)k));
        for (int i = (int)k; i < arr.size(); i++) {
            if (arr.get(i) > result.get(0)) {
                result.set(0, arr.get(i));
                int j = 1;
                while (j < k && result.get(j) < arr.get(i)) {
                    result.set(j, result.get(j-1));
                    j++;
                }
                result.set(j-1, arr.get(i));
