        class Node {
            long value;
            long index;
            Node(long value, long index) {
                this.value = value;
                this.index = index;
            }
        }
        PriorityQueue<Node> queue = new PriorityQueue<Node>(Comparator.comparing(n -> n.value));
        ArrayList<Long> result = new ArrayList<Long>();
        for (int i = 0; i < arr.size(); i++) {
            queue.add(new Node(arr.get(i), i));
        }
        for (int i = 0; i < k; i++) {
            result.add(queue.poll().value);
        }
        return result;

