        return array.stream().sorted(Comparator.comparingLong(i -> {
            int sum = 0;
            for (long j : array) {
                if (j < i) {
                    sum += j;
                }
            }
            return sum % 2 == 0 ? i : -i;
        })).collect(Collectors.toCollection(ArrayList::new));

