            return array;
        }
        long sum = 0;
        for (int i = 0; i < array.size(); i++) {
            sum += array.get(i);
        }
        if (sum % 2 == 0) {
            array.sort(Collections.reverseOrder());
        } else {
            array.sort(Collections.reverseOrder());
        }
        return array;

