    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int size = array.size();
        if (size == 0) {
            return array;
        }
        if (size == 1) {
            return array;
        }
        if (size == 2) {
            if ((array.get(0) + array.get(1)) % 2 == 0) {
                return array;
            } else {
                ArrayList<Long> temp = new ArrayList<Long>();
                temp.add(array.get(1));
                temp.add(array.get(0));
                return temp;
            }
        }
        long first = array.get(0);
        long last = array.get(size - 1);
        if ((first + last) % 2 == 0) {
            ArrayList<Long> temp = new ArrayList<Long>();
            for (int i = 0; i < size; i++) {
                temp.add(array.get(i));
            }
            return temp;
        } else {
            ArrayList<Long> temp = new ArrayList<Long>();
            for (int i = size - 1; i >= 0; i--) {
                temp.add(array.get(i));
            }
            return temp;
        }

