            return array;
        }
        ArrayList<Long> odd = new ArrayList<>();
        ArrayList<Long> even = new ArrayList<>();
        for (long i : array) {
            if ((i + array.get(array.size() - 1)) % 2 == 0) {
                even.add(i);
            } else {
                odd.add(i);
            }
        }
        if (odd.size() == 0) {
            return even;
        }
        if (even.size() == 0) {
            return odd;
        }
        if (odd.get(0) > even.get(0)) {
            odd.addAll(even);
            return odd;
