    // (0l)
    // >>> isBored(("The sky is blue. The sun is shining. I love this weather"))
    // (1l)
    public static long isBored(String S) {
        int count = 0;
        int i = 0;
        while (i < S.length()) {
            int j = S.indexOf(".", i);
            int k = S.indexOf("!", i);
            int l = S.indexOf("?", i);
            int m = 0;
            if (j == -1) m = k;
            if (k == -1) m = j;
            if (l == -1) m = k;
            if (j != -1 && k != -1) m = Math.min(j, k);
            if (j != -1 && l != -1) m = Math.min(j, l);
            if (k != -1 && l != -1) m = Math.min(k, l);
            if (m != 0) {
                if (S.substring(i, m).startsWith("I")) count++;
                i = m + 1;
            } else {
                break;
            }
        }
        return count;

