    // Return 0 if all digits are even.
    // For example:
    // >>> digits((1l))
    // (1l)
    // >>> digits((4l))
    // (0l)
    // >>> digits((235l))
    // (15l)
    public static long digits(long n) {
        long product = 1;
        while (n > 0) {
            int lastDigit = (int)(n % 10);
            if (lastDigit % 2 != 0) {
                product *= lastDigit;
            }
            n = n / 10;
        }
        return product;

