            if(ch[i] == 'a' || ch[i] == 'e' || ch[i] == 'i' || ch[i] == 'o' || ch[i] == 'u'){
                rep += (char)(ch[i] + 2);
            }
            else{
                rep += (char)(ch[i] + 1 - 'a');
            }
        }
        return rep;

