        int substringPointer = 0;
        for (int pointer = 0; pointer < string.length(); pointer++) {
            if (string.charAt(pointer) == substring.charAt(substringPointer)) {
                if (substringPointer == substring.length() - 1) return (pointer - substringPointer + 1) * 1l;
                substringPointer++;
            } else {
                substringPointer = 0;
            }
        }
        return (string.length() - substringPointer + 1) * 1l;

