    // ("Example_1")
    // >>> fixSpaces((" Example 2"))
    // ("_Example_2")
    // >>> fixSpaces((" Example 3"))
    // ("_Example-3")
    public static String fixSpaces(String text) {
        StringBuilder s = new StringBuilder();
        int count = 0;
        for(int i = 0; i < text.length(); i++) {
            char c = text.charAt(i);
            if(c == ' ') {
                if(count == 0) {
                    s.append("_");
                }
                else if(count == 1) {
                    s.append("-");
                }
                count++;
            }
            else {
                s.append(c);
                count = 0;
            }
        }
        return s.toString();

