        ArrayList<Long> notes = new ArrayList<Long>();
        for (String note : music_string.split("\\s")){
            switch (note) {
                case "o":
                    notes.add(4L);
                    break;
                case "o|":
                    notes.add(2L);
                    break;
                case ".|":
                    notes.add(1L);
                    break;
