        int[] even = new int[(int) (n / 2)];
        int[] odd = new int[(int) (n / 2)];
        for (long i = 1; i <= n; i++) {
            boolean oddCheck = true;
            int j = (int) (i / 10);
            if (i <= 9 || i >= n / 2 || (i % 10 == 0 && i > 10)) {
                continue;
            }
            for (int k = 0; k < j; k++) {
                if (i / Math.pow(10, k) % 10 != i / Math.pow(10, j - k) % 10) {
                    oddCheck = false;
                    break;
                }
            }
            if (oddCheck) {
                odd[j - 1]++;
            } else {
                even[j - 1]++;
            }
        }
        for (int i = 0; i < even.length; i++) {
            res = res.add(even[i], odd[i]);
        }
        return res;

