    // Note:
    // 1. 1 <= n <= 10^3
    // 2. returned pair has the number of even and odd integer palindromes respectively.
    public static Pair<Long, Long> evenOddPalindrome(long n) {
        boolean[][] dp = new boolean[n+1][2];
        for (int i = 1; i <= n; ++i) {
            dp[i][0] = dp[i][1] = false;
            int j = 1;
            for (long k = i; j <= 5 && k <= n; ++j, k *= 10) {
                int l = k % 10;
                if (l >= 5) {
                    dp[k][0] = dp[k][1] = true;
                    break;
                } else if (l < 2) {
                    if (j == 1) {
                        dp[k][0] = dp[k][1] = true;
                        break;
                    } else {
                        dp[k][0] = dp[k][1] = false;
                        break;
                    }
                } else {
                    dp[k][0] = dp[k][1] = dp[k / 10][0];
                }
            }
            if (i % 2 == 0) {
                for (int j = 1; j <= 5 && i * j <= n; ++j) {
                    long k = i * j;
                    dp[k][0] = true;
                    if (j == 1 || j == 2) {
                        dp[k][1] = true;
                    } else {
                        dp[k][1] = false;
                    }
                }
            } else {
                for (int j = 1; j <= 5 && i * j <= n; ++j) {
                    long k = i * j;
                    dp[k][1] = true;
                    if (j == 1 || j == 2) {
                        dp[k][0] = true;
                    } else {
                        dp[k][0] = false;
                    }
                }
            }
        }
        long even = 0;
        long odd = 0;
        for (int i = 1; i <= n; ++i) {
            if (dp[i][0]) {
                ++even;
            }
            if (dp[i][1]) {
                ++odd;
            }
        }
        return Pair.with(even, odd);

