            if (i % 10 == 0) {
                long m = 1;
                while (i / m >= 10) {
                    m *= 10;
                }
                long d = i / m;
                d = (d * (d + 1) / 2) * m;
                if (d > i) {
                    d = d - i;
                    if (d % 2 == 0) {
                        evens += d / 2;
