                        evens += (d - 1) / 2;
                        odds += 1;
                    }
                } else {
                    d = i - d + 1;
                    if (d % 2 == 0) {
                        evens += d / 2;
                    } else {
                        evens += (d - 1) / 2;
                        odds += 1;
                    }
                }
            }
        }
        return Pair.with(evens, odds);

