    public static long fizzBuzz(long n) {
        long[] divisors = { 11, 13 };
        long[] dividends = { 0, 7 };
        long[] counts = new long[divisors.length];
        for (long i = 1; i < n; i++) {
            for (int j = 0; j < divisors.length; j++) {
                if (i % divisors[j] == 0) {
                    counts[j] += ((i / divisors[j]) * dividends[j]);
                }
            }
        }
        long total = Arrays.stream(counts).sum();
        return total;

