    // Example
    // >>> solve((1000l))
    // ("1")
    // >>> solve((150l))
    // ("110")
    // >>> solve((147l))
    // ("1100")
    // Variables:
    // @N integer
    // Constraints: 0 ≤ N ≤ 10000.
    // Output:
    // a string of binary number
    public static String solve(long N) {
        String bin = Long.toBinaryString(N);
        int sum = 0;
        for(int i = 0; i < bin.length(); i++) {
            if(bin.charAt(i) == '1') {
                sum += 1;
            }
        }
        return Integer.toString(sum);

