    testcases = 0
    while True:
        solution = input()
        problem = Problem()
        for test in problem.get_test_cases(solution):
            solution_output = str(eval(solution))
            if solution_output == test:
                testcases += 1
            else:
                print("Wrong answer:", solution_output, "should be:", test)
        if testcases == 5:
            print("Great! All test cases passed.")
            return


