    public List<Integer> ans = new ArrayList<>();
    public void solution(List<List<Integer>> lists) {
        int n = lists.get(0).get(0);
        List<Integer> list = lists.get(0);
        list.remove(0);
        lists.remove(0);
        int i = 1;
        while(list.size() != 0) {
            int x = list.get(0);
            List<Integer> new_list = new ArrayList<>();
            for(Integer a : lists) {
                if(a.get(0) != a.get(1) && (x % a.get(0) == 0 || x % a.get(1) == 0)) {
                    new_list.add(a);
                }
            }
            if(new_list.size() == 0) {
                ans.add(i);
                list.remove(0);
                i++;
            } else {
                for(int j = 0; j < lists.size(); j++) {
                    if(new_list.get(0).equals(lists.get(j))) {
                        lists.remove(j);
                        break;
                    }
                }
                list.remove(0);
                i++;
            }
