        boolean isUpper = true, isLower = true;
        for(Map.Entry<String,String> entry : dict.entrySet()){
            if(Character.isUpperCase(entry.getKey().charAt(0))){
                isLower = false;
                continue;
            }else if(Character.isLowerCase(entry.getKey().charAt(0))){
                isUpper = false;
                continue;
            }else return false;
        }
        return isUpper || isLower;

