    // (2l)
    public static long add(ArrayList<Long> lst) {
        ArrayList<Long> evenOdd = new ArrayList<Long>();
        for (long i = 0; i < lst.size(); i++) {
            if (lst.get(i) % 2 == 0) {
                if (i % 2 == 0) {
                    evenOdd.add(lst.get(i));
                }
            }
        }
        if (evenOdd.size() == 0) {
            return 0;
        }
        long answer = 0;
        for (long i = 0; i < evenOdd.size(); i++) {
            answer += evenOdd.get(i);
        }
        return answer;

