class Problem {
    // Given a non-empty array list of integers lst. pair the numbers that have the same sum.
    // Examples:
    // >>> pair((new ArrayList<Long>(Arrays.asList((long)4l, (long)2l, (long)6l, (long)7l))))
    // (((4l), (7l)), ((2l), (6l)))
    public static Object pair(ArrayList<Long> lst) {
        ArrayList<Long> even = new ArrayList<>();
        ArrayList<Long> odd = new ArrayList<>();
        for(int i = 0; i < lst.size(); i++) {
            if(i % 2 == 0)
                even.add(lst.get(i));
            else
                odd.add(lst.get(i));
        }
        Object[] result = new Object[even.size()];
        for(int i = 0; i < even.size(); i++) {
            long evenSum = 0;
            for(int j = 0; j < odd.size(); j++)
                evenSum += odd.get(j);
            result[i] = new Tuple<>(even.get(i), evenSum);
        }
        return result;

