            if(c >= 'a' && c <= 'z'){
                res += (char)((c + 2 - 'a') % 26 + 'a');
            }else if(c >= 'A' && c <= 'Z'){
                res += (char)((c + 2 - 'A') % 26 + 'A');
            }else{
                res += c;
            }
        }
        return res;

