                b[i] = (char)('a' + (c - 'a' + 2 * 26) % 26);
            }
            else if(c >= 'A' && c <= 'Z'){
                b[i] = (char)('A' + (c - 'A' + 2 * 26) % 26);
            }
            else{
                b[i] = c;
            }
        }
        return new String(b);

