    public static ArrayList<Long> parseMusic(String music_string) {
        ArrayList<Long> noteList = new ArrayList<Long>();
        if (music_string.length() == 0)
            return noteList;
        int index = 0;
        while (index < music_string.length()) {
            if (music_string.charAt(index) == ' ') {
                index++;
                continue;
            }
            if (music_string.substring(index, Math.min(index + 4, music_string.length())).equals("o o|")) {
                noteList.add((long) 4);
                index += 4;
            } else if (music_string.substring(index, Math.min(index + 4, music_string.length())).equals("o| .|")) {
                noteList.add((long) 2);
                index += 4;
            } else if (music_string.substring(index, Math.min(index + 2, music_string.length())).equals("o|")) {
                noteList.add((long) 2);
                index += 2;
            } else if (music_string.substring(index, Math.min(index + 2, music_string.length())).equals("o ")) {
                noteList.add((long) 4);
                index += 2;
            } else {
                noteList.add((long) 1);
                index++;
            }
        }
        return noteList;

