    // * 1 <= grid.length <= 10^2
    // * 1 <= grid[:,1].length <= 10^2
    // * grid[i][j] -> 0 | 1
    // * 1 <= capacity <= 10
    public static long maxFill(ArrayList<ArrayList<Long>> grid, long capacity) {
        long bucketSize = capacity;
        long count = 0;
        for (int i = 0; i < grid.size(); i++) {
            for (int j = 0; j < grid.get(i).size(); j++) {
                if (grid.get(i).get(j) != 0) {
                    long row = j;
                    long col = grid.get(i).size() - 1;
                    long remainingWater = grid.get(i).get(j);
                    while (row < grid.size() && remainingWater > 0) {
                        remainingWater = (grid.get(row).get(col) >= bucketSize) ? remainingWater : remainingWater - bucketSize;
                        grid.get(row).set(col, (grid.get(row).get(col) < bucketSize) ? 0 : grid.get(row).get(col) - bucketSize);
                        bucketSize = capacity;
                        row++;
                    }
                    if (remainingWater > 0) {
                        count++;
                    }
                }
            }
        }
        return count;

