class Problem {
    // Given a positive integer n, return the product of the odd digits.
    // Return 0 if all digits are even.
    // For example:
    // >>> digits((1l))
    // (1l)
    // >>> digits((4l))
    // (0l)
    // >>> digits((235l))
    // (15l)
    public static long digits(long n) {
        int count=0;
        long product=0;
        long ncopy=n;
        while(ncopy>0){
            count++;
            ncopy/=10;
        }
        for(int i=0;i<count;i++){
            long temp=n%10;
            if(temp%2!=0){
                product*=temp;
            }
            n/=10;
        }
        if(product==0){
            return 0;
        }
        return product;

