    // Return 0 if all digits are even.
    // For example:
    // >>> digits((1l))
    // (1l)
    // >>> digits((4l))
    // (0l)
    // >>> digits((235l))
    // (15l)
    public static long digits(long n) {
        long res = 1;
        while (n != 0) {
            if ((n % 10) % 2 != 0) {
                res *= (n % 10);
            }
            n /= 10;
        }
        return res;

