    // (3l)
    // >>> howManyTimes(("aaaa"), ("aa"))
    // (3l)
    public static long howManyTimes(String string, String substring) {
        if (string.equals(substring)) {
            return 1l;
        } else if (substring.length() > string.length()) {
            return 0l;
        }
        int substringLength = substring.length();
        int remainingLength = string.length() - substringLength;
        return (long) (remainingLength / substringLength) + (remainingLength % substringLength == 0 ? 1l : 0l);

