        ArrayList<Long> sortedArr = new ArrayList<>(arr);
        Collections.sort(sortedArr);
        ArrayList<Long> result = new ArrayList<Long>();
        for (int i = 0; i < sortedArr.size(); i++) {
            if (k == 0) {
                break;
            }
            result.add(sortedArr.get(i));
            k--;
        }
        return result;

