    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        var result = new ArrayList<Long>();
        for (int i = 0; i < arr.size() - k; i++) {
            for (int j = i + 1; j < arr.size(); j++) {
                if (arr.get(j) > arr.get(i)) {
                    var tmp = arr.get(j);
                    arr.set(j, arr.get(i));
                    arr.set(i, tmp);
                }
            }
        }
        for (int i = 0; i < k; i++) {
            result.add(arr.get(i));
        }
        return result;

