        ArrayList<Long> result = new ArrayList<Long>(k);
        for (long num : arr) {
            if (result.size() < k) {
                result.add(num);
            } else {
                int i = 0;
                while (i < k) {
                    if (result.get(i) < num) {
                        break;
                    }
                    i++;
                }
                if (i < k) {
                    result.set(i, num);
                }
            }
        }
        return result;

