        ArrayList<Long> res = new ArrayList<Long>();
        PriorityQueue<Long> queue = new PriorityQueue<Long>(
            (a, b) -> a < b ? 1 : a > b ? -1 : 0);
        for (long i : arr) {
            if (queue.size() < k) {
                queue.add(i);
            }
            else {
                long min = queue.peek();
                if (i > min) {
                    queue.remove();
                    queue.add(i);
                }
            }
        }
        return new ArrayList<Long>(queue);

