class Problem {
    // For a given number n, find the largest number that divides n evenly, smaller than n
    // >>> largestDivisor((15l))
    // (5l)
    public static long largestDivisor(long n) {
        // Base case:
        if (n <= 3) {
            return n - 1;
        }
        // Step 1: Find the square root of n
        BigInteger sqrtN = BigInteger.valueOf((long) Math.sqrt(n));
        // Step 2: Get all possible divisors
        ArrayList<BigInteger> possibleDivisors = new ArrayList<>();
        for (long i = 1; i <= sqrtN.longValue(); i++) {
            if (n % i == 0) {
                possibleDivisors.add(BigInteger.valueOf(i));
                if (i != n / i) {
                    possibleDivisors.add(BigInteger.valueOf(n / i));
                }
            }
        }
        // Step 3: Get the last number in the list
        return possibleDivisors.get(possibleDivisors.size() - 1).longValue();

