    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        if (array.size() == 0) return array;
        if (array.size() == 1) return array;
        ArrayList<Long> ascending = array.stream().sorted().collect(Collectors.toCollection(ArrayList::new));
        ArrayList<Long> descending = array.stream().sorted(Collections.reverseOrder()).collect(Collectors.toCollection(ArrayList::new));
        if (array.get(0) + array.get(array.size() - 1) % 2 == 0) return ascending;
        return descending;

