    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int sum = 0;
        for (int i = 0; i < array.size(); i++) {
            sum += array.get(i);
        }
        ArrayList<Long> sorted = new ArrayList<Long>();
        if (sum % 2 == 0) {
            while (array.size() > 0) {
                Long max = array.get(0);
                for (int i = 1; i < array.size(); i++) {
                    if (array.get(i) > max) {
                        max = array.get(i);
                    }
                }
                array.remove(array.indexOf(max));
                sorted.add(max);
            }
        } else {
            while (array.size() > 0) {
                Long min = array.get(0);
                for (int i = 1; i < array.size(); i++) {
                    if (array.get(i) < min) {
                        min = array.get(i);
                    }
                }
                array.remove(array.indexOf(min));
                sorted.add(min);
            }
        }
        return sorted;

