    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        if(array.size() <= 1){
            return array;
        }
        boolean isSumEven = (array.get(0) + array.get(array.size()-1)) % 2 == 0;
        int length = array.size();
        long[] arrayCopy = new long[length];
        for(int i = 0; i < length; i++){
            arrayCopy[i] = array.get(i);
        }
        if(isSumEven){
            Arrays.sort(arrayCopy);
