            Arrays.sort(arrayCopy, (o1, o2) -> {
                if(o1 > o2){
                    return -1;
                }
                if(o1 < o2){
                    return 1;
                }
                return 0;
            });
        }
        ArrayList<Long> result = new ArrayList<Long>();
        for(int i = 0; i < length; i++){
