    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int i = 0;
        while (i < array.size()) {
            int j = i + 1;
            while (j < array.size()) {
                if ((array.get(i) + array.get(j)) % 2 == 0) {
                    // descending
                    if (array.get(i) < array.get(j)) {
                        array.set(i, array.get(i) + array.get(j));
                        array.set(j, array.get(i) - array.get(j));
                        array.set(i, array.get(i) - array.get(j));
                    }
                } else {
                    // ascending
                    if (array.get(i) > array.get(j)) {
                        array.set(i, array.get(i) + array.get(j));
                        array.set(j, array.get(i) - array.get(j));
                        array.set(i, array.get(i) - array.get(j));
                    }
                }
                j++;
            }
            i++;
        }
        return array;

