    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        ArrayList<Long> sorted = new ArrayList<>(array);
        for (int i = 1; i < sorted.size(); ++i) {
            for (int j = 0; j < sorted.size() - i; ++j) {
                if ((sorted.get(j) + sorted.get(j + 1)) % 2 == 0) {
                    if (sorted.get(j) > sorted.get(j + 1)) {
                        Long tmp = sorted.get(j);
                        sorted.set(j, sorted.get(j + 1));
                        sorted.set(j + 1, tmp);
                    }
                } else {
                    if (sorted.get(j) < sorted.get(j + 1)) {
                        Long tmp = sorted.get(j);
                        sorted.set(j, sorted.get(j + 1));
                        sorted.set(j + 1, tmp);
                    }
                }
            }
        }
        return sorted;

