        if (array.size() == 0 || array.size() == 1) {
            return array;
        }
        ArrayList<Long> result = new ArrayList<Long>();
        int sum = 0;
        for (int i = 0; i < array.size(); i++) {
            sum += array.get(i);
        }
        int step = sum % 2 == 0 ? 1 : -1;
        int start = 0;
        int end = array.size() - 1;
        while (start <= end) {
            result.add(array.get(start));
            result.add(array.get(end));
            start += step;
            end += step;
