    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        array.sort((a, b) -> {
            if ((a + array.get(array.size() - 1)) % 2 == (b + array.get(array.size() - 1)) % 2) {
                return 0;
            }
            else if ((a + array.get(array.size() - 1)) % 2 != (b + array.get(array.size() - 1)) % 2) {
                if ((a + array.get(array.size() - 1)) % 2 == 1) {
                    return -1;
                }
                else {
                    return 1;
                }
            }
            else {
                return 0;
            }
        });
        return array;

