    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int n = array.size();
        int m = n % 2 == 0 ? n / 2 - 1 : n / 2;
        for (int i = 0; i < m; i++) {
            int sum = array.get(i) + array.get(n - i - 1);
            if (sum % 2 == 0) {
                ArrayList<Long> copy = new ArrayList<>(array);
                copy.set(i, array.get(n - i - 1));
                copy.set(n - i - 1, array.get(i));
                array = copy;
            }
        }
        return array;

