    public static ArrayList<Long> f(long n) {
        ArrayList<Long> list = new ArrayList<Long>();
        for (int i = 1; i <= n; i++) {
            if (i % 2 == 0) {
                long sum = 1;
                for (int j = 1; j <= i; j++)
                    sum = sum * j;
                list.add(sum);
            } else {
                long sum = 0;
                for (int j = 1; j <= i; j++)
                    sum = sum + j;
                list.add(sum);
            }
        }
        return list;

