        ArrayList<Long> results = new ArrayList<Long>();
        for (long i = 1; i <= n; i++) {
            if (i % 2 == 0) {
                results.add((long)Math.pow(i, i));
            }
            else {
                results.add((long)i * (i + 1) / 2);
            }
        }
        return results;

